import HighlightedQuote from "../components/quotes/HighlightedQuote";
import QuoteList from "../components/quotes/QuoteList";

const QuoteDetails= ()=>{
    const STATIC_QUOTES=[
        {id:'quote1',author:"Pravallika", text:"Your patience is your power"},
        {id:'quote2',author:"Mrudula", text:"Kindness always comes back"},
        {id:'quote3',author:"Chintu", text:"Focus on what matters"}
    ];
    
      const params=useParams();
      const quote=STATIC_QUOTES.filter(qt=>qt.id===params.quoteId);
    if(QuoteList.length===0)
    return <p>No Quotes Found!!</p>
    return (
        <HighlightedQuote text={quote[0].text} author={quote[0].author}/>
    );
};

export default QuoteDetails;
