import { useParams, Route, Link, useLocation } from "react-router-dom";
import Comments from "../components/comments/Comments";
import { Fragment } from "react";
import HighlightedQuote from "../components/quotes/HighlightedQuote";
import QuoteList from "../components/quotes/QuoteList";

const QuoteDetails = () => {
  const location = useLocation();
  const STATIC_QUOTES = [
    { id: "quote1", author: "Pravallika", text: "Your patience is your power" },
    { id: "quote2", author: "Mrudula", text: "Kindness always comes back" },
    { id: "quote3", author: "Chintu", text: "Focus on what matters" },
  ];

  const params = useParams();
  const quote = STATIC_QUOTES.filter((qt) => qt.id === params.quoteId);
  if (QuoteList.length === 0) return <p>No Quotes Found!!</p>;
  return (
    <Fragment>
      <HighlightedQuote text={quote[0].text} author={quote[0].author} />
      <div className="centered">
        <Link to={`${location.pathname}/comments`}>Load Comments</Link>
      </div>
      <Route path={`${location.pathname}/comments`}>
        <Comments />
      </Route>
    </Fragment>
  );
};

export default QuoteDetails;
